import { Update } from "@material-ui/icons";
import { DisplaySettingsRounded } from "@mui/icons-material";
import axios from "axios";
import React, { useState } from 'react';
import "../components/Navbar.css";

export default function Type () {
    const [nbPokeType, setnbPokeType] = useState("");
    const [namePokeType, setnamePokeType] = useState("");
    var nbpok=[];
    var nb=0;

    const getType = async (type) => {
        try {
            const url = `https://pokeapi.co/api/v2/type/${type}`
            const res = await axios.get(url)
            setnbPokeType(res.data.pokemon.length);
            setnamePokeType(res.data.name);

            console.log(res)
        } catch (e) {
            console.log(e)
        };
    } 

    var appel= (num) => {

        getType(num);
        nb=nbPokeType;
        nbpok[num]=nb;
    }
    var appelNormal =() =>{
        appel(1);
    }

    return (
        <div class="App">

            <div className="widget">

                {appel(2)}

                <div class="btn-group" role="group" aria-label="Basic mixed styles example">
                    <button type="button" class="btn btn-danger" onClick="appelNormal()">Fight</button>
                    <button type="button" class="btn btn-warning">Middle</button>
                    <button type="button" class="btn btn-success">Right</button>
                </div>
            </div>
        </div>
    );

};
